INSERT INTO rule_entity (id, rule, name, description)
VALUES
    (1, 'USEROF:DEBIT%NOTUSEROF:INVEST%TOPUPGTSPENDLESS:1000', 'Invest 500', 'Откройте свой путь к успеху с индивидуальным инвестиционным счетом (ИИС) от нашего банка! Воспользуйтесь налоговыми льготами и начните инвестировать с умом. Пополните счет до конца года и получите выгоду в виде вычета на взнос в следующем налоговом периоде. Не упустите возможность разнообразить свой портфель снизить риски и следить за актуальными рыночными тенденциями. Откройте ИИС сегодня и станьте ближе к финансовой независимости!'),
    (2, 'USEROF:DEBIT%USEROF:DEBIT%ACTIVEUSERANDPAYABLE:SAVING:10000:5%TOPUPGTSPEND:DEBIT', 'Простой кредит', 'Откройте свою собственную «Копилку» с нашим банком! «Копилка» — это уникальный банковский инструмент который поможет вам легко и удобно накапливать деньги на важные цели. Больше никаких забытых чеков и потерянных квитанций — всё под контролем! Преимущества «Копилки»: Накопление средств на конкретные цели. Установите лимит и срок накопления и банк будет автоматически переводить определенную сумму на ваш счет. Прозрачность и контроль. Отслеживайте свои доходы и расходы контролируйте процесс накопления и корректируйте стратегию при необходимости. Безопасность и надежность. Ваши средства находятся под защитой банка а доступ к ним возможен только через мобильное приложение или интернет-банкинг. Начните использовать «Копилку» уже сегодня и станьте ближе к своим финансовым целям!'),
    (3, 'USEROF:DEBIT%USEROF:SAVING%ACTIVEUSERANDPAYABLE:SAVING:10000:5%TOPUPGTSPEND:DEBIT', 'Простой кредит', 'Откройте свою собственную «Копилку» с нашим банком! «Копилка» — это уникальный банковский инструмент который поможет вам легко и удобно накапливать деньги на важные цели. Больше никаких забытых чеков и потерянных квитанций — всё под контролем! Преимущества «Копилки»: Накопление средств на конкретные цели. Установите лимит и срок накопления и банк будет автоматически переводить определенную сумму на ваш счет. Прозрачность и контроль. Отслеживайте свои доходы и расходы контролируйте процесс накопления и корректируйте стратегию при необходимости. Безопасность и надежность. Ваши средства находятся под защитой банка а доступ к ним возможен только через мобильное приложение или интернет-банкинг. Начните использовать «Копилку» уже сегодня и станьте ближе к своим финансовым целям!'),
    (4, 'NOTUSEROF:CREDIT%TOPUPGTSPEND:DEBIT%SPENDSGT:DEBIT:100000', 'Top Saving', 'Откройте мир выгодных кредитов с нами! Ищете способ быстро и без лишних хлопот получить нужную сумму? Тогда наш выгодный кредит — именно то что вам нужно! Мы предлагаем низкие процентные ставки гибкие условия и индивидуальный подход к каждому клиенту. Почему выбирают нас: Быстрое рассмотрение заявки. Мы ценим ваше время поэтому процесс рассмотрения заявки занимает всего несколько часов. Удобное оформление. Подать заявку на кредит можно онлайн на нашем сайте или в мобильном приложении. Широкий выбор кредитных продуктов. Мы предлагаем кредиты на различные цели: покупку недвижимости автомобиля образование лечение и многое другое. Не упустите возможность воспользоваться выгодными условиями кредитования от нашей компании!');